package if_statment;

public class Ex4 {

	public static void main(String[] args) {
		// TODO Auto-generated method stub
		
		/*
		 * 기본문법
		 * if(조건식){
		 * 
		 * } else if{
		 * 
		 * } else if{
		 * 
		 * }.....{
		 * 
		 * } else {
		 * 
		 * }
		 *
		 * */
		
		int num =3;
		if(num>5) {
			System.out.println("크다");
		} else if (num<5) {
			System.out.println("작다");
		} else {
			System.out.println("같다");
		}
		
		
		
		
	}

}
